CREATE TABLE attendees (id BIGINT AUTO_INCREMENT, email VARCHAR(255) NOT NULL, entrytime DATETIME DEFAULT '0000-00-00 00:00:00' NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE programme (id INT AUTO_INCREMENT, fk_talks INT NOT NULL, room VARCHAR(32) NOT NULL, date DATE NOT NULL, timestart INT NOT NULL, timeend TIME NOT NULL, type text NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE speakers (id INT AUTO_INCREMENT, name VARCHAR(32) NOT NULL, profession VARCHAR(32), city VARCHAR(32) NOT NULL, company VARCHAR(32) NOT NULL, twitter VARCHAR(32), email VARCHAR(64) NOT NULL, website VARCHAR(64), img VARCHAR(32), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE sponsors (id INT AUTO_INCREMENT, sponsor VARCHAR(32) NOT NULL, website VARCHAR(128) NOT NULL, package INT, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE talks (id INT AUTO_INCREMENT, fk_speakers INT NOT NULL, title TEXT NOT NULL, abstract TEXT, datepreferencefortalk text, linktopresentation INT, PRIMARY KEY(id)) ENGINE = INNODB;
